import React, { useState } from 'react';
import './App.css';

function App() {
  const [revealedImages, setRevealedImages] = useState([]);
  const [gameOver, setGameOver] = useState(false);

  const images = [
    'jellycat1.jpg', 'jellycat2.jpg', 'jellycat3.jpg', 'jellycat4.jpg', 'jellycat5.jpg', 'jellycat6.jpg',
  ];

  const totalImages = 6; // Total number of images to reveal
  const buttonPositions = Array.from({ length: 12 }, (_, index) => index + 1); // 12 button positions

  const handleButtonClick = () => {
    if (revealedImages.length < totalImages) {
      const remainingImages = images.filter(image => !revealedImages.includes(image));
      const randomImage = remainingImages[Math.floor(Math.random() * remainingImages.length)];
      setRevealedImages([...revealedImages, randomImage]);

      if (revealedImages.length + 1 === totalImages) {
        setGameOver(true);
      }
    }
  };

  return (
    <div className="flex flex-col justify-center items-center h-screen p-4">
      <div className="grid grid-cols-3 gap-4 p-4"> {/* Grid layout for buttons */}
        {buttonPositions.map((pos) => (
          <button
            key={pos}
            className="bg-gradient-to-r from-violet-200 to-pink-200 text-white font-bold py-2 px-6 rounded-lg shadow-md transition duration-300 ease-in-out transform hover:scale-105"
            onClick={handleButtonClick}
            disabled={gameOver}
          >
            Find Jellycat {pos}
          </button>
        ))}
      </div>
      <div className="mt-4 grid grid-cols-3 gap-4">
        {revealedImages.map((image, index) => (
          <img key={index} src={image} alt="Cute Jellycat" className="max-w-full rounded-lg shadow-lg" />
        ))}
      </div>
      {gameOver && (
        <div className="mt-4 bg-white p-4 rounded-lg shadow-lg max-w-md text-center">
          Game Over! You found all the Jellycats!
        </div>
      )}
    </div>
  );
}

export default App;
