import React, { useState } from 'react';
import './App.css';

function App() {
  const [imageIndex, setImageIndex] = useState(0);
  const [foundButtons, setFoundButtons] = useState([false, false, false, false, false, false]);

  const images = [
    'dogs1.jpg', 'dogs2.jpg', 'dogs3.jpg', 'dogs4.jpg', 'dogs5.jpg',
    'dogs6.jpg'
  ];

  const buttonPositions = [
    { top: '20%', left: '10%' },
    { top: '50%', left: '25%' },
    { top: '70%', left: '40%' },
    { top: '30%', left: '60%' },
    { top: '60%', left: '80%' },
    { top: '80%', left: '50%' }
  ];

  const handleClick = (index) => {
    const newFoundButtons = [...foundButtons];
    newFoundButtons[index] = true;
    setFoundButtons(newFoundButtons);

    if (foundButtons.every((found) => found)) {
      setImageIndex((prevIndex) => (prevIndex + 1) % images.length);
      setFoundButtons([false, false, false, false, false, false]);
    }
  };

  return (
    <div className="relative h-screen p-4">
      {foundButtons.some((found) => found) && (
        <img src={images[imageIndex]} alt="Cute dog" className="absolute top-10 left-1/2 transform -translate-x-1/2 mb-4 max-w-md rounded-lg shadow-lg" />
      )}
      {buttonPositions.map((position, index) => (
        <button
          key={index}
          className={`absolute bg-gradient-to-r from-violet-200 to-pink-200 text-white font-bold py-2 px-6 rounded-lg shadow-md transition duration-300 ease-in-out transform hover:scale-105 ${foundButtons[index] ? 'hidden' : ''}`}
          style={{ top: position.top, left: position.left }}
          onClick={() => handleClick(index)}
        >
          Find Me!
        </button>
      ))}
    </div>
  );
}

export default App;
