import React, { useState } from 'react';
import './App.css';

function App() {
  const [imageIndex, setImageIndex] = useState(0);
  const [buttonText, setButtonText] = useState('Show Pups');

  const images = [
    'dogs1.jpg', 'dogs2.jpg', 'dogs3.jpg', 'dogs4.jpg', 'dogs5.jpg',
    'dogs6.jpg'
  ];

  const handleClick = () => {
    if (buttonText === 'Show Pups') {
      setButtonText('Next Pup');
    }
    setImageIndex((prevIndex) => (prevIndex + 1) % images.length);
  };

  return (
    <div className="flex justify-center items-center h-screen">
      <div>
        {buttonText !== 'Show Pups' && (
          <img src={images[imageIndex]} alt="Cute dog" className="mx-auto mb-4 max-w-xs" />
        )}
        <button className="bg-blue-500 hover:bg-blue-700 text-white font-bold py-2 px-4 rounded-full" onClick={handleClick}>
          {buttonText}
        </button>
      </div>
    </div>
  );
}

export default App;
