import React, { useState } from "react";
import { FontAwesomeIcon } from "@fortawesome/react-fontawesome";
import { faCircle } from "@fortawesome/free-solid-svg-icons";

const cuteImages = [
  "cute1.png",
  "cute2.png",
  "cute3.png",
  "cute4.png",
  "cute5.png",
]; // Replace with actual image paths

function App() {
  const [revealedImages, setRevealedImages] = useState([]);

  const handleButtonClick = (index) => {
    if (!revealedImages.includes(index)) {
      setRevealedImages([...revealedImages, index]);
    }
  };

  return (
    <div className="relative flex min-h-screen flex-col items-center px-2 pt-2">
      {/* Background layer */}
      <div
        className="absolute left-0 top-0 h-screen w-full bg-white"
        style={{
          backgroundColor: "#ffffff",
          opacity: 0.5,
          backgroundSize: "110px 110px, 110px 110px, 22px 22px, 22px 22px",
          backgroundImage:
            "linear-gradient(#f4f4f4 4.4px, transparent 4.4px), linear-gradient(90deg, #f4f4f4 4.4px, transparent 4.4px), linear-gradient(#f4f4f4 2.2px, transparent 2.2px), linear-gradient(90deg, #f4f4f4 2.2px, #ffffff 2.2px)",
          backgroundPosition:
            "-4.4px -4.4px, -4.4px -4.4px, -2.2px -2.2px, -2.2px -2.2px",
          zIndex: 0,
        }}
      />

      {/* Main Content */}
      <div className="relative z-10 mx-auto flex h-screen w-full max-w-6xl flex-col justify-between overflow-hidden">
        <div className="flex-grow flex flex-col justify-center items-center md:pt-10">
          <div className="text-center w-full mt-24">
            <h1 className="text-4xl md:text-7xl font-bold text-zinc-800 md:text-balance">
              Find the Hidden Treasures!
            </h1>
            <h2 className="text-grey opacity-50 mx-auto max-w-2xl text-lg md:text-xl font-medium my-5">
              Click the buttons to reveal cute images and have fun!
            </h2>
          </div>
        </div>

        <div className="relative flex flex-wrap justify-center items-center h-full w-full p-4">
          {Array.from({ length: 10 }).map((_, index) => (
            <button
              key={index}
              onClick={() => handleButtonClick(index)}
              className="relative m-4 p-4 rounded-full bg-gradient-to-r from-pink-300 to-purple-400 shadow-lg hover:shadow-xl transition-all duration-300 ease-in-out transform hover:scale-110"
            >
              <FontAwesomeIcon
                icon={faCircle}
                className="text-white text-2xl"
              />
              {revealedImages.includes(index) && (
                <div className="absolute top-0 left-0 w-full h-full flex items-center justify-center bg-white bg-opacity-80 rounded-full">
                  <img
                    src={cuteImages[index % cuteImages.length]}
                    alt="Cute"
                    className="rounded-full"
                  />
                </div>
              )}
            </button>
          ))}
        </div>
      </div>
    </div>
  );
}

export default App;
