import React, { useState } from 'react';
import './App.css';
import useSound from 'use-sound'; // To play sound clips

function App() {
  const [imageIndex, setImageIndex] = useState(null);
  const [messageIndex, setMessageIndex] = useState(null);
  const [showMessage, setShowMessage] = useState(false);
  const [showImage, setShowImage] = useState(false);
  const [play] = useSound('sound.mp3'); // Add a sound clip in your public folder

  const images = [
    'dogs1.jpg', 'dogs2.jpg', 'dogs3.jpg', 'dogs4.jpg', 'dogs5.jpg',
    'dogs6.jpg'
  ];

  const messages = [
    'You are amazing!',
    'You make my heart melt!',
    'Every day with you is an adventure!',
    'You are my sunshine!',
    'Love you to the moon and back!',
  ];

  const handleImageClick = () => {
    setShowMessage(false);
    setImageIndex(Math.floor(Math.random() * images.length));
    setShowImage(true);
  };

  const handleMessageClick = () => {
    setShowImage(false);
    setMessageIndex(Math.floor(Math.random() * messages.length));
    setShowMessage(true);
  };

  const handleSoundClick = () => {
    play();
  };

  return (
    <div className="flex flex-col justify-center items-center h-screen p-4">
      <div className="grid grid-cols-2 gap-4 p-4"> {/* Create a grid layout */}
        <button
          className="bg-gradient-to-r from-violet-200 to-pink-200 text-white font-bold py-2 px-6 rounded-lg shadow-md transition duration-300 ease-in-out transform hover:scale-105"
          onClick={handleImageClick}
        >
          Reveal a Cute Image
        </button>
        <button
          className="bg-gradient-to-r from-blue-200 to-green-200 text-white font-bold py-2 px-6 rounded-lg shadow-md transition duration-300 ease-in-out transform hover:scale-105"
          onClick={handleMessageClick}
        >
          Show a Sweet Message
        </button>
        <button
          className="bg-gradient-to-r from-yellow-200 to-red-200 text-white font-bold py-2 px-6 rounded-lg shadow-md transition duration-300 ease-in-out transform hover:scale-105"
          onClick={handleSoundClick}
        >
          Play a Sound
        </button>
      </div>
      {showImage && imageIndex !== null && (
        <img src={images[imageIndex]} alt="Cute dog" className="mt-4 max-w-md rounded-lg shadow-lg" />
      )}
      {showMessage && messageIndex !== null && (
        <div className="mt-4 bg-white p-4 rounded-lg shadow-lg max-w-md text-center">
          {messages[messageIndex]}
        </div>
      )}
    </div>
  );
}

export default App;
